    public float moveSpeed = 100f;
    public bool moveDown;
    [HideInInspector] public bool spare;

    GameObject P1, P2;

    private void Start()
    {
        P1 = GameObject.FindGameObjectWithTag("Player");
        P2 = GameObject.FindGameObjectWithTag("Player2");
        //path1 = Application.dataPath;
        //path2 = Application.dataPath;
        spare = false;
    }

    private void Update()
    {
        if (!moveDown)
        {
            transform.position += new Vector3(moveSpeed * Time.deltaTime, 0, 0);
            if (transform.position.x > P1.transform.position.x || transform.position.x > P2.transform.position.x)
                Application.Quit();
        }
        else
        {
            transform.position -= new Vector3(0, moveSpeed * Time.deltaTime, 0);
            if (transform.position.y + (transform.localScale.y / 2) < P1.transform.position.y || transform.position.y + (transform.localScale.y / 2) < P2.transform.position.y)
                Application.Quit();
        }
    }

    private void OnCollisionEnter2D(Collision2D collision)
    {
        if (!spare)
        {
            if (collision.gameObject.tag == "Player" || collision.gameObject.tag == "Player2")
            {
                /*var file = File.Open(path1, FileMode.OpenOrCreate, FileAccess.ReadWrite);
